from django.db import modelsfrom django.contrib.auth.models import Userfrom object.models import Appsfrom imagekit.models import ImageSpecFieldfrom imagekit.processors import ResizeToFillfrom django.contrib.staticfiles.templatetags.staticfiles import staticclass UserProfile(models.Model):    user = models.OneToOneField(User, unique=True, on_delete=models.CASCADE)    birth = models.DateField(blank=True, null=True)    phone = models.CharField(max_length=20, null=False)    weixin = models.CharField(max_length=50, null=False)    company = models.CharField(max_length=50, null=False)    title = models.CharField(max_length=50, null=True)    def __str__(self):        return 'user{}'.format(self.user.username)class UserInfo(models.Model):    user = models.OneToOneField(User, unique=True, on_delete=models.CASCADE)    field = models.CharField(max_length=50, null=True)    style = models.CharField(max_length=50, null=True)    def __str__(self):        return 'user {} info'.format(self.user.username)class UserApps(models.Model):    user = models.OneToOneField(User, unique=True, on_delete=models.CASCADE)    apps = models.ForeignKey(Apps,on_delete=models.CASCADE,related_name='关联APP')    def __str__(self):        return 'user {0} - app {1}'.format(self.user.username,self.apps.APP_ID)class HeadPortrait(models.Model):    user = models.OneToOneField(User, unique=True, on_delete=models.CASCADE)    avatar = models.ImageField(upload_to='user_head_portrait/')    avatar_thumbnail_small = ImageSpecField(        source='avatar',        processors=[ResizeToFill(30,30)],        format='JPEG',        options={'quality':60},    )    avatar_thumbnail_large = ImageSpecField(        source='avatar',        processors=[ResizeToFill(90, 90)],        format='JPEG',        options={'quality': 120},    )